CREATE TABLE registro (id BIGINT AUTO_INCREMENT, serial BIGINT, sid BIGINT, fecha DATETIME, ip VARCHAR(16), uid VARCHAR(200), usuario VARCHAR(200), email VARCHAR(200), apellido VARCHAR(200), nombre VARCHAR(200), edad BIGINT, sexo VARCHAR(1), localidad VARCHAR(200), provincia VARCHAR(200), pais VARCHAR(200), empresa VARCHAR(200), sitio VARCHAR(200), asistencia TINYINT(1), taller_android TINYINT(1), taller_moodle TINYINT(1), install_fest TINYINT(1), certificado TINYINT(1), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE user (id INT AUTO_INCREMENT, username VARCHAR(128) NOT NULL UNIQUE, email VARCHAR(128) NOT NULL UNIQUE, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '0', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX name_idx_idx (username), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE user_activation_code (user_id BIGINT, code VARCHAR(128), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id)) ENGINE = INNODB;
